Item.method_code:
require(['dijit/registry'], function(registry) {
	if (item && item.getAttribute('action') !== 'add') {
		var sidebar = registry.byId('sidebar');
		sidebar.switchSidebarButton(inArgs.control.id, '../images/TreeGridView.svg', true);
		sidebar.switchSidebarButton('rb_TreeGridConfiguratorShowForm', '../Images/ShowFormOff.svg', false);
		window.configuratorView.showConfigurator();
	}
});
